import { Rewrite } from '@rewritejs/sdk';

const rewrite = new Rewrite(process.env.REWRITE_API_KEY!);

const projectId = '748395130237498368';

const response = await rewrite.projects.update(projectId, {
  name: 'Alertes marketing',
});

package main

import (
  "context"
  "log"

  rewrite "github.com/rewritetoday/golang"
)

func main() {
  client, err := rewrite.New("rw_live_your_api_key")
  if err != nil { log.Fatal(err) }

  projectID := "748395130237498368"

  _, err = client.Projects.Update(context.Background(), projectID, rewrite.RESTPatchUpdateProjectBody{
    Name: "Alertes marketing",
  })
  if err != nil { log.Fatal(err) }
}

from rewrite import Rewrite
import requests

client = Rewrite(api_key='rw_live_your_api_key')

response = requests.request(
    'PATCH',
    'https://api.rewritetoday.com/v1/projects/748395130237498368',
    headers={
        'Authorization': "Bearer rw_live_your_api_key",
        'Content-Type': 'application/json',
    },
    json={
      "name": "Alertes marketing"
    },
)
print(response.json())

var client = HttpClient.newHttpClient();
var json = "{\"name\":\"Alertes marketing\"}";
var requestBuilder = HttpRequest.newBuilder()
  .uri(URI.create("https://api.rewritetoday.com/v1/projects/748395130237498368"))
  .header("Authorization", "Bearer rw_live_your_api_key")
  .header("Content-Type", "application/json")
  .method("PATCH", HttpRequest.BodyPublishers.ofString(json));
var response = client.send(requestBuilder.build(), HttpResponse.BodyHandlers.ofString());

using var client = new HttpClient();
using var request = new HttpRequestMessage();
request.Method = HttpMethod.Patch;
request.RequestUri = new Uri("https://api.rewritetoday.com/v1/projects/748395130237498368");
request.Headers.Add("Authorization", "Bearer rw_live_your_api_key");
request.Content = new StringContent("{\"name\":\"Alertes marketing\"}", Encoding.UTF8, "application/json");
var response = await client.SendAsync(request);
var content = await response.Content.ReadAsStringAsync();

import { Rewrite } from '@rewritejs/sdk';

