await rewrite.projects.delete(projectId);

package main

import (
  "context"
  "log"

  rewrite "github.com/rewritetoday/golang"
)

func main() {
  client, err := rewrite.New("rw_live_your_api_key")
  if err != nil { log.Fatal(err) }

  projectID := "748395130237498368"

  if err := client.Projects.Delete(context.Background(), projectID); err != nil {
    log.Fatal(err)
  }
}

